                                                $dayTime = explode(' ', $key->open_now);
                                                if (count($dayTime) >= 2) {
                                                    $day = explode('-', $dayTime[0]);
                                                    $startday = strtolower($day[0]);
                                                    $endday = strtolower($day[1]);
                                                    $time = explode('-', $dayTime[1]);
